mapper.select:
SELECT A.BOARD_ID boardId , A.ARTICLE_ID articleId , D.USER_NAME writerName , D.FK_FILE writerImage , A.TITLE title , A.HIT_CNT hitCount , CASE WHEN A.CREATED_USER_ID = #{userId} THEN true ELSE false END editable , (CASE WHEN B.CNT > 0 THEN true ELSE false END) isAttachedFile , B.CNT fileCount , CASE WHEN C.HIT_CNT > 0 THEN true ELSE false END isRead FROM GRWARTICLE A LEFT OUTER JOIN ( SELECT X.ARTICLE_ID , COUNT(*) CNT FROM GRWARTICLEFILE X GROUP BY X.ARTICLE_ID ) B ON (A.ARTICLE_ID = B.ARTICLE_ID) LEFT OUTER JOIN GRWARTICLECHECK C ON (A.ARTICLE_ID = C.ARTICLE_ID AND C.USER_ID = #{userId}) LEFT OUTER JOIN COMUSER D ON (A.CREATED_USER_ID = D.USER_ID) WHERE 1=1 AND A.BOARD_ID = #{data.boardId}
AND A.TITLE LIKE #{data.title}
AND A.CONTENTS LIKE #{data.contents}
ORDER BY A.ARTICLE_ID DESC limit #{pagesize} + 1 offset #{pagenumber} * #{pagesize}

select b.SYS_DT as sysDt , b.SYS_USER as sysUser , u.name as sysUsernm , b.UPD_DT as updDt , b.UPD_USER as updUser , b.PK_ARTICLE as pkArticle , b.PPK_ARTICLE as ppkArticle , b.TITLE as title , b.CONTENTS as contents , b.PWD as pwd , b.HIT_CNT as hitCnt , b.FROM_DT as fromDt , b.TO_DT as toDt , b.seq as seq , 'Y' as checkYn , (select group_concat(z.PK_FILE,'|',z.FILE_NM,'|',z.size order by z.PK_FILE SEPARATOR ',') from grarticle_files x inner join cmfileinfo z on (x.PK_FILE = z.PK_FILE) where x.PK_ARTICLE = b.PK_ARTICLE ) as fileInfo -- uuid | path | name | size , b.FK_BOARD as fkBoard from grboard a inner join grarticle b on (a.PK_BOARD = b.FK_BOARD) left outer join user u on (b.SYS_USER = u.USERNAME) where a.PK_BOARD = #{fkBoard}
and b.title like #{title}
and b.contents like #{contents}
order by b.PK_ARTICLE desc
